package com.ethernom.android.etherapi;

abstract public class ServiceEventListener {

    public abstract void onWriteSuccess(int resultCode);
    public abstract void onWriteFail(int resultCode, int hwSpecificError);

    public abstract void onServiceClosedSucess(int resultCode);
    public abstract void onServiceClosedFail(int resultCode,  int hwSpecificError);
    public abstract void onServiceClosedByService(int resultCode);

}
